<?phpclass Magestore_ManualInvoiceNumber_Block_Adminhtml_Items extends Mage_Adminhtml_Block_Sales_Order_Invoice_Create_Items {		protected function _beforeToHtml() {		$onclick = "submitAndReloadArea($('invoice_item_container'),'".$this->getUpdateUrl()."')";		$this->setChild(				'update_button',				$this->getLayout()->createBlock('adminhtml/widget_button')->setData(array(						'class'     => 'update-button',						'label'     => Mage::helper('sales')->__('Update Qty\'s'),						'onclick'   => $onclick,				))		);		$this->_disableSubmitButton = true;		$_submitButtonClass = ' disabled';		foreach ($this->getInvoice()->getAllItems() as $item) {				if ($item->getQty() || $this->getSource()->getData('base_grand_total')) {						$this->_disableSubmitButton = false;						$_submitButtonClass = '';						break;				}		}		$_submitLabel = $this->getOrder()->getForcedDoShipmentWithInvoice()?'Submit Invoice and Shipment':'Submit Invoice';		$this->setChild(				'submit_button',				$this->getLayout()->createBlock('adminhtml/widget_button')->setData(array(						'label'     => Mage::helper('sales')->__('%s', $_submitLabel),						'class'     => 'save submit-button' . $_submitButtonClass,						'onclick'   => '$(\'edit_form\').submit()',						'disabled'  => $this->_disableSubmitButton				))		);		$this->setTemplate('manualinvoicenumber/items.phtml');		return parent::_prepareLayout();	}}